import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './AuthStyles.css';  // Asegúrate de que el nombre del archivo CSS coincida

const Login = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleSubmit = (event) => {
    event.preventDefault();
    // Lógica de autenticación aquí
    console.log('Login:', username, password);
    // navigate('/somewhere'); // Navegar a otra página después de login si es necesario
  };

  return (
    <div className="auth-container">
      <h1>Login</h1>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Username"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
        />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        <button type="submit">Log In</button>
        <button type="button" onClick={() => navigate('/register')}>Registrarte</button>
      </form>
    </div>
  );
};

export default Login;
