import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './AuthStyles.css';

const Login = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleSubmit = (event) => {
    event.preventDefault();
    // Simulación de autenticación
    if (username === 'admin' && password === 'admin') {
      console.log('Login successful:', username);
      navigate('/game'); // Navegar a la página del juego después del login exitoso
    } else {
      alert('Username or password incorrect.');
    }
  };

  return (
    <div className="auth-container">
      <img src={`${process.env.PUBLIC_URL}/clickalm.png`} alt="Clickalm" style={{ width: 'auto', margin: '0 auto 20px' }} />
      <h1>Login</h1>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Username"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
        />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        <button type="submit">Log In</button>
        <button type="button" onClick={() => navigate('/register')}>Registrarte</button>
      </form>
    </div>
  );
};

export default Login;
